import React from "react";
import {Link} from "react-router-dom";
import { useState, useRef } from 'react';
import Axios from 'axios';
import {ReCAPTCHA} from "react-google-recaptcha";



function UserLogin() {
    const [selectedRole] = useState('Yoga Practitioner');
    const [SuccessMsg, setSuccessMsg] = useState("")
    const [ErrorMsg, setErrorMsg] = useState("")
    const [valid_token, setValidToken] = useState([]);

    const SITE_KEY = process.env.REACT_APP_reCAPTCHA_SITE_KEY;
    const SECRET_KEY = process.env.REACT_APP_reCAPTCHA_SECRET_KEY;
    const captchaRef = useRef(null);
    const verifyToken = async (token) => {
        let APIResponse = [];

        try {
            let response = await Axios.post(`http://localhost:8000/verify-token`, {
                reCAPTCHA_TOKEN: token,
                Secret_Key: SECRET_KEY,
            });

            APIResponse.push(response['data']);
            return APIResponse;
        } catch (error) {
            console.log(error);
        }
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        let token = captchaRef.current.getValue();
        captchaRef.current.reset();

        if (token) {
            let valid_token = await verifyToken(token);
            setValidToken(valid_token);

            if (valid_token[0].success === true) {
                console.log("verified");
                setSuccessMsg("Hurray!! you have submitted the form")
            } else {
                console.log("not verified");
                setErrorMsg(" Sorry!! Verify you are not a bot")
            }
        }
    }
    console.log(selectedRole);
    return (
        <div>
            <form name="userLogin" /*action="/authUser"*/ method="post" onSubmit={handleSubmit}>
                <div className="container">
                    <div className={'container'}>
                        <div className={'pinkFont'}>
                            <h1>User Login</h1>
                        </div>
                    </div>
                    <p>Please fill in this form for Logging In.</p>
                    <hr/>
                    <label htmlFor="username"><b>Username</b></label>
                    <input type="text" placeholder="Enter Username" name="username" id="username" required/>

                    <label htmlFor="psw"><b>Password</b></label>
                    <input type="password" placeholder="Enter Password" name="psw" id="psw" required/>
                    <ReCAPTCHA
                        className="recaptcha"
                        sitekey={SITE_KEY}
                        ref={captchaRef}
                    />
                    <p>By creating an account you agree to our <Link to={""}>Terms & Privacy</Link>.</p>
                    <button type="submit" className="registerbtn">Login</button>
                </div>

                <div className="container signin">
                    <p>Don't Have an account? <Link to={'/userregis'}> Register Here</Link>.</p>
                </div>
            </form>
        </div>)
};
export default UserLogin
