import React, {useState} from 'react';
import axios from 'axios';
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const FormComponent = () => {
    const [formData, setFormData] = useState({
        fullname: '',
        age: '',
        gender: '',
        reason: '',
        doneyoga: '',
        yes: '',
        userMobile: '',
        illness: '',
        reference: '',
    });
    const [gender, setGender] = useState("Female")
    const onOptionChange = e => {
        setGender(e.target.value)
    }

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const res = await axios.post('/inquiry', formData);
            toast.success('Form submitted successfully');
        } catch (error) {
            console.error(error);
            toast.error('Already Inquired');
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="container">
                    <div className="container">
                        <div className="pinkFont">
                            <h1>Inquiry</h1>
                        </div>
                    </div>
                    <p>Please fill in this form for Submitting Your Inquiry.</p>
                    <hr/>
                    <label htmlFor="fullname"><b>Full Name</b></label>
                    <input
                        type="text"
                        placeholder="Enter Your Full Name"
                        name="fullname"
                        id="fullname"
                        value={formData.fullname}
                        onChange={handleChange}
                        required
                    />

                    <label htmlFor="age"><b>Age</b></label>
                    <input
                        type="number"
                        placeholder="Enter Your Age"
                        name="age"
                        id="age"
                        value={formData.age}
                        onChange={handleChange}
                        required
                    />

                    <label htmlFor="gender"><b>Gender</b></label>
                    <div className={"container"}>
                        <input
                            type="radio"
                            name="gender"
                            value="Male"
                            id="Male"
                            checked={gender === "Male"}
                            onChange={onOptionChange}
                        />
                        <label htmlFor="Male">Male</label>

                        <input
                            type="radio"
                            name="gender"
                            value="Female"
                            id="Female"
                            checked={gender === "Female"}
                            onChange={onOptionChange}
                        />
                        <label htmlFor="Female">Female</label>

                        <input
                            type="radio"
                            name="gender"
                            value={formData.gender}
                            id="Other"
                            checked={gender === "Other"}
                            onChange={handleChange}
                        />
                        <label htmlFor="Other">Other</label>

                        <p>
                            Selected gender <strong>{gender}</strong>
                        </p>
                    </div>

                    <label htmlFor="reason"><b>Reason For Joining Yoga</b></label>
                    <input type="textarea"
                           placeholder="Enter Reason"
                           name="reason"
                           id="reason"
                           value={formData.reason}
                           onChange={handleChange} required/>

                    <label htmlFor="yes"><b>Have you been practising yoga lately?</b></label>
                    <input type="text"
                           placeholder="Enter Your Answer"
                           name="doneyoga"
                           id="doneyoga"
                           required/>

                    <label htmlFor="yes"><b>If the answer is "YES," where and how long have you been trying to
                        practice?</b></label>
                    <input type="text"
                           placeholder="Enter Your Answer"
                           name="yes"
                           id="yes"
                           value={formData.yes}
                           onChange={handleChange} required/>

                    <label htmlFor="userMobile"><b>Mobile Number</b></label>
                    <input type="number"
                           placeholder="Enter Your Mobile Number"
                           name="userMobile"
                           id="userMobile"
                           value={formData.userMobile}
                           onChange={handleChange}
                           required/>

                    <label htmlFor="illness"><b>Any illness or discomfort?</b></label>
                    <input type="text"
                           placeholder="Enter Your Answer"
                           name="illness"
                           id="illness"
                           value={formData.illness}
                           onChange={handleChange}required/>

                    <label htmlFor="reference"><b>Reference: - Social Media / Print Media / Other?</b></label>
                    <input type="text"
                           placeholder="Enter Your Reference"
                           name="reference"
                           id="reference"
                           value={formData.reference}
                           onChange={handleChange}required/>

                    <p>On Submitting you agreeing to our <a href="/">Terms & Condition</a>.</p>
                </div>

                <button type="submit" className="registerbtn">Submit</button>
            </form>
            <ToastContainer/>
        </div>
    );
};

export default FormComponent;
